import React, { useState } from "react";
import "./App.scss";
import TodoList from "./components/TodoList";

function App() {
  const [todoList, setTodoList] = useState([
    {
      id: 1,
      title: "abc",
    },
    {
      id: 2,
      title: "abdc",
    },
    {
      id: 3,
      title: "abec",
    },
    {
      id: 4,
      title: "abrc",
    },
  ]);

  function handleTodoClick(todo) {
    console.log(todo);
  }

  return (
    <div className="app">
      <h1>React Hooks- TodoList</h1>
      <TodoList todos={todoList} todoOnClick={handleTodoClick} />
    </div>
  );
}

export default App;
